INSERT INTO company (id, name, domain, website, headline, number_of_employees, funding, industry, country, competitive_landscape, updated_at) VALUES ('fc633895-5b43-4e52-9671-ae8ce7f26468', 'adidas', 'adidas.com', 'https://adidas.com', 'Adidas is a global leader in the design, development, and marketing of athletic and lifestyle footwear, apparel, and accessories. The company focuses on innovation and performance, offering products that cater to athletes and fashion-conscious consumers alike. Adidas is renowned for its iconic three-stripe logo and its commitment to sustainability, integrating eco-friendly materials and practices into its product lines. With a strong presence in sports such as soccer, running, and basketball, Adidas differentiates itself through cutting-edge technology and collaborations with high-profile designers and athletes.', 59000, 47900000000, 'Manufacturing', 'Germany', 'Competitive landscape analysis is being prepared. This section will provide insights into market positioning, competitive pressures, and strategic considerations based on available company and competitor data.', '2025-11-20T05:33:24.443369');
INSERT INTO company (id, name, domain, website, headline, number_of_employees, funding, industry, country, competitive_landscape, updated_at) VALUES ('d75c0c6f-3e1e-4e21-92b1-860ecc5a83e7', 'New Balance', 'newbalance.com', 'https://newbalance.com', 'New Balance is a global athletic footwear and apparel company known for its commitment to quality and performance. The company specializes in designing and manufacturing a wide range of products including running shoes, lifestyle sneakers, and sports apparel. With a focus on innovation and comfort, New Balance caters to athletes and casual wearers alike, emphasizing a blend of style and functionality. Notably, the company differentiates itself by maintaining a significant portion of its manufacturing in the United States and the United Kingdom, ensuring high standards of craftsmanship.', 7000, NULL, 'Manufacturing', 'United States', 'Competitive landscape analysis is being prepared. This section will provide insights into market positioning, competitive pressures, and strategic considerations based on available company and competitor data.', '2025-11-22T18:39:50.242730');
INSERT INTO company (id, name, domain, website, headline, number_of_employees, funding, industry, country, competitive_landscape, updated_at) VALUES ('6d97435d-de3d-41d2-b531-c0a4c7c9a976', 'PUMA', 'puma.com', 'https://puma.com', 'PUMA is a leading global sports brand that designs, develops, sells, and markets footwear, apparel, and accessories. The company focuses on performance and sport-inspired lifestyle products, catering to athletes and fashion-conscious consumers alike. Known for its innovative designs and collaborations with high-profile athletes and designers, PUMA differentiates itself through a commitment to sustainability and a strong presence in both the sports and fashion industries. Its market focus spans various sports, including soccer, running, training, golf, and motorsports.', 18071, 15000000000, 'Manufacturing', 'Germany', 'Competitive landscape analysis is being prepared. This section will provide insights into market positioning, competitive pressures, and strategic considerations based on available company and competitor data.', '2025-11-22T18:39:50.242730');
INSERT INTO company (id, name, domain, website, headline, number_of_employees, funding, industry, country, competitive_landscape, updated_at) VALUES ('f3322252-b29c-45ab-a102-fc8e8a07b9a3', 'OnePlus', 'oneplus.com', 'https://oneplus.com', 'OnePlus is a global technology company that designs and manufactures premium smartphones and accessories. Known for its high-performance devices, OnePlus focuses on delivering flagship-level features at competitive prices, catering primarily to tech-savvy consumers and enthusiasts. The company differentiates itself through its commitment to quality, innovative design, and a strong community-driven approach. OnePlus''s product lineup includes smartphones, audio devices, and wearable technology, all of which emphasize cutting-edge technology and user-centric design.', NULL, NULL, 'Manufacturing', 'United Kingdom', 'Competitive landscape analysis is being prepared. This section will provide insights into market positioning, competitive pressures, and strategic considerations based on available company and competitor data.', '2025-11-22T18:39:50.242730');
INSERT INTO company (id, name, domain, website, headline, number_of_employees, funding, industry, country, competitive_landscape, updated_at) VALUES ('b8ba8536-1ce8-4ebf-a2c3-16e3ec13d3a6', 'Mercedes-Benz', 'mercedes-benz.de', 'https://mercedes-benz.de', 'Mercedes‑Benz is a German luxury automotive brand headquartered in Stuttgart. It designs, manufactures, and markets a wide range of premium passenger cars, sport‑utility vehicles, coupes, convertibles, and electric models, as well as offering related financial services such as leasing, banking, and mobility solutions across Germany and worldwide.', 201, NULL, 'Finance', 'Germany', NULL, '2025-11-24T11:48:56.390800');
INSERT INTO company (id, name, domain, website, headline, number_of_employees, funding, industry, country, competitive_landscape, updated_at) VALUES ('3d5d5d58-fdf1-4e40-b22e-f4c01c8216c9', 'BMW', 'bmwgroup.com', 'https://bmwgroup.com', 'BMW is a global premium automotive group headquartered in Germany, producing luxury cars, SUVs, motorcycles and offering financial services through its BMW Financial Services. The company operates a worldwide production network of over 30 sites and sells in more than 140 markets, emphasizing sustainability, electric and hydrogen mobility, autonomous driving, artificial intelligence and digital services.', 51, NULL, 'Manufacturing', 'Germany', NULL, '2025-11-27T15:31:24.449489');
INSERT INTO company (id, name, domain, website, headline, number_of_employees, funding, industry, country, competitive_landscape, updated_at) VALUES ('21b8c726-89cd-4507-9401-578703afe797', 'Audi', 'audiusa.com', 'https://audiusa.com', 'Audi of America is the U.S. subsidiary of the German premium automotive brand, offering a range of luxury sedans, SUVs, sportbacks, convertibles and electric e‑tron models. The company focuses on performance, advanced technology, driver assistance and electric mobility, selling new and certified pre‑owned vehicles through a nationwide dealer network and providing financial services, accessories, and after‑sales support.', 1000, NULL, 'Retail', 'United States', NULL, '2025-11-22T18:39:50.242730');
INSERT INTO company (id, name, domain, website, headline, number_of_employees, funding, industry, country, competitive_landscape, updated_at) VALUES ('c84e214e-859d-416c-99a8-c72fa7caa93b', 'Genesis', 'genesis.com', 'https://genesis.com', 'Genesis is a premium automotive brand offering a range of luxury vehicles—including sedans, SUVs, electric models, and high‑performance concept cars—designed for discerning customers worldwide. The brand emphasizes sophisticated design, advanced technology, and a global dealer network across regions such as Europe, Korea, the USA, China, and Australia.', 501000, NULL, 'Manufacturing', 'South Korea', NULL, '2025-11-24T12:21:51.317183');
INSERT INTO company (id, name, domain, website, headline, number_of_employees, funding, industry, country, competitive_landscape, updated_at) VALUES ('9143d909-2a72-47c2-bdf5-0b6be66fee4f', 'ASICS', 'asics.com', 'https://asics.com', 'ASICS is a global leader in the sporting goods industry, specializing in the design and manufacture of high-performance athletic footwear, apparel, and accessories. The company is renowned for its innovative approach to sports technology, offering products that cater to a wide range of sports including running, tennis, and training. ASICS focuses on enhancing athletic performance and promoting a healthy lifestyle, with a strong emphasis on research and development to ensure superior quality and functionality. Notable for its GEL cushioning technology, ASICS distinguishes itself through a commitment to craftsmanship and a dedication to supporting athletes worldwide.', 9000, NULL, 'Retail', 'Japan', 'Competitive landscape analysis is being prepared. This section will provide insights into market positioning, competitive pressures, and strategic considerations based on available company and competitor data.', '2025-11-13T14:43:35.449534');
INSERT INTO company (id, name, domain, website, headline, number_of_employees, funding, industry, country, competitive_landscape, updated_at) VALUES ('a3c94799-3804-4e74-8c87-92665814cc9d', 'porsche', 'porsche.com', 'https://porsche.com', 'Porsche is a German luxury automotive manufacturer that designs, engineers and markets high‑performance sports cars, sedans, SUVs and electric vehicles. Operating worldwide through an extensive dealer network, the brand is known for its distinctive design, advanced technology and motorsport heritage.', 10000, NULL, 'Manufacturing', 'United States', 'Porsche operates in the luxury automotive market, specializing in high-performance sports cars, sedans, SUVs, and electric vehicles. It positions itself as a premium brand that combines distinctive design with advanced technology and a strong motorsport heritage, setting it apart from competitors such as Mercedes-Benz, BMW, Audi, and Genesis. The main competitive pressures in this market include the rapid advancement of electric vehicle technology, shifting consumer preferences towards sustainability, and the ongoing need for innovation in performance and design. 
INSERT INTO company (id, name, domain, website, headline, number_of_employees, funding, industry, country, competitive_landscape, updated_at) VALUES ('bfae8002-0f82-46d7-9393-6924829fe506', 'La Comer', 'lacomer.com.mx', 'https://lacomer.com.mx', 'La Comer is a Mexican retail chain that operates an online grocery store, offering a wide range of food, beverage, and household products for families across Mexico. Customers can shop online and have their orders delivered directly to their doors. The company focuses on convenient e‑commerce, fresh produce, and everyday essentials.', 1000, NULL, 'Retail', 'Mexico', NULL, '2025-11-24T19:56:36.921313');
INSERT INTO company (id, name, domain, website, headline, number_of_employees, funding, industry, country, competitive_landscape, updated_at) VALUES ('15cc2d2b-ea82-46a3-bb3f-71d5e48a6a1f', 'McDonald''s', 'mcdonalds.com', 'https://www.mcdonalds.com', 'McDonald''s is a global fast-food restaurant chain that specializes in serving a variety of quick-service meals, including hamburgers, fries, chicken products, breakfast items, and beverages. With a strong market focus on affordability and convenience, McDonald''s positions itself as a leader in the fast-food industry, catering to a diverse customer base across various demographics. Key differentiators include its iconic menu items, such as the Big Mac and Happy Meal, as well as its extensive global presence and commitment to innovation in service delivery, including digital ordering and delivery options.', NULL, NULL, 'Fast Food', NULL, NULL, NULL);
INSERT INTO company (id, name, domain, website, headline, number_of_employees, funding, industry, country, competitive_landscape, updated_at) VALUES ('5a97cd8f-f4cc-4d29-82ae-89985c68b1dd', 'walmart', 'walmart.com', 'https://walmart.com.mx', 'Walmart is a leading Mexican e‑commerce retailer offering a wide range of products from groceries and household items to electronics, apparel, and personal care. Operating through walmart.com.mx, it provides fast, secure delivery, low‑price guarantees, coupons, and financing options such as interest‑free months, while also promoting its Walmart Pass membership for free shipping and additional perks.', 501000, NULL, 'Retail', 'Mexico', 'Walmart operates primarily in the Mexican e-commerce market, focusing on a diverse range of products including groceries, electronics, apparel, and personal care items. It positions itself as a low-cost leader, leveraging its extensive product offerings and competitive pricing strategies to attract customers away from rivals such as Amazon and Sam''s Club. The main competitive pressures include the need to maintain low prices, enhance delivery speeds, and improve customer service amidst growing competition from both traditional retailers and online platforms.
INSERT INTO company (id, name, domain, website, headline, number_of_employees, funding, industry, country, competitive_landscape, updated_at) VALUES ('4fbeb612-6ca3-4ae0-a64d-f63f5c239872', 'Telenet Group', 'telenet.be', 'https://telenet.be', 'Telenet Group is a leading telecommunications and media company in Belgium, providing a wide range of services including cable television, broadband internet, and mobile and fixed-line telephony. The company focuses on delivering high-quality connectivity and entertainment solutions to both residential and business customers. Telenet is known for its innovative offerings and strong customer service, positioning itself as a key player in the Belgian telecom market. Its commitment to technological advancement and customer satisfaction distinguishes it from competitors.', 3200, 2900000000, 'Telecommunications', 'Belgium', NULL, '2025-11-24T19:20:49.431647');
INSERT INTO company (id, name, domain, website, headline, number_of_employees, funding, industry, country, competitive_landscape, updated_at) VALUES ('31a89ad7-f1ae-46a8-8ac3-43810d7d0429', 'Sam''s Club', 'sams.com.mx', 'https://sams.com.mx', 'Sam''s Club México is the Mexican online branch of the Sam''s Club membership warehouse club, operated by Walmart. It offers members bulk discounts on a wide range of products including groceries, electronics, home goods, furniture, and more, with options for online shopping and home delivery across Mexico.', 11, NULL, 'Retail', 'Mexico', NULL, '2025-11-24T13:59:23.391579');
INSERT INTO company (id, name, domain, website, headline, number_of_employees, funding, industry, country, competitive_landscape, updated_at) VALUES ('b1f75b4e-640c-4843-95cb-3402918eb58d', 'Liverpool', 'liverpool.com.mx', 'https://liverpool.com.mx', 'Liverpool is a leading Mexican department store and e‑commerce retailer offering a broad selection of products for the home, fashion, electronics, appliances, toys, and more. It operates a network of physical stores across Mexico and an online shop that provides services such as free shipping, click‑and‑collect, financing, and a loyalty program.', 10000, NULL, 'Retail', 'Mexico', NULL, '2025-11-22T19:25:58.824686');
INSERT INTO company (id, name, domain, website, headline, number_of_employees, funding, industry, country, competitive_landscape, updated_at) VALUES ('00aa41fb-3b96-4a70-9c68-e22aa6a478da', 'Bodega Aurrera', 'bodegaaurrera.com.mx', 'https://bodegaaurrera.com.mx', 'Bodega Aurrera is a Mexican discount supermarket chain owned by Walmart that offers online grocery shopping and home delivery at low prices. The platform allows customers to browse a wide range of food, household, and personal care products, place orders online, and receive them at their doorsteps across Mexico.', 5000, NULL, 'Retail', 'Mexico', NULL, '2025-11-28T20:40:51.113581');
INSERT INTO company (id, name, domain, website, headline, number_of_employees, funding, industry, country, competitive_landscape, updated_at) VALUES ('3394cad9-0b8d-4e15-8506-922eb2b46243', 'Huawei', 'huawei.com', 'https://www.huawei.com', 'Huawei is a global leader in information and communications technology (ICT) infrastructure and smart devices. The company offers a wide range of products and services, including telecommunications networks, IT solutions, smart devices, and cloud services. Huawei focuses on providing innovative and customer-centric solutions to telecom carriers, enterprises, and consumers worldwide. Notable for its extensive R&D capabilities and commitment to innovation, Huawei is a key player in the development of 5G technology and digital transformation initiatives.', 207000, NULL, 'Technology', 'China', NULL, '2025-11-22T18:39:50.242730');
