INSERT INTO company (id, name, domain, website, headline, number_of_employees, funding, industry, country, competitive_landscape, updated_at) VALUES ('e81cb0a0-6376-45a5-871e-f119c3873758', 'OPPO', 'oppo.com', 'https://oppo.com', 'OPPO is a leading global technology company specializing in the design and manufacture of innovative smartphones, smart devices, audio products, and IoT solutions. The company is renowned for its cutting-edge camera technology, sleek design, and advanced mobile operating systems, catering primarily to tech-savvy consumers and young professionals. OPPO''s market focus includes Asia, Europe, and other international markets, where it differentiates itself through continuous innovation, high-quality products, and a strong emphasis on user experience. Notable aspects of OPPO include its pioneering work in mobile photography and fast-charging technology.', 40000, NULL, 'Manufacturing', 'China', NULL, '2025-11-22T19:25:58.824686');
