INSERT INTO company (id, name, domain, website, headline, number_of_employees, funding, industry, country, competitive_landscape, updated_at) VALUES ('34a770eb-7811-4f29-bbbb-db8bc7bad71b', 'Giant Bicycles', 'giant-bicycles.com', 'https://www.giant-bicycles.com', 'Giant Bicycles is a leading global manufacturer of bicycles and cycling gear, renowned for its innovation and high-quality products. The company offers a wide range of bicycles, including road, mountain, electric, and lifestyle bikes, as well as cycling accessories and apparel. Giant focuses on providing advanced cycling solutions for enthusiasts and professionals alike, emphasizing performance, design, and technology. Its commitment to craftsmanship and cutting-edge engineering distinguishes Giant as a prominent player in the cycling industry.', NULL, NULL, 'Bicycles', 'Taiwan', NULL, '2025-11-24T13:05:23.611351');
